    function cipher() {
  var ciphered = (document.getElementById("ciphertext").value.toUpperCase()).replace(/[A-Z]/g, L => String.fromCharCode((L.charCodeAt(0) % 26) + 65));
  document.getElementById("ciphermessage").innerHTML = ciphered;
}; 
$("#ciphertext").on("keyup", function() {
  cipher();
});
    function palindrome() {
  var palin = (document.getElementById("palinput").value).replace(/[\W_]/g, '').toLowerCase();
  var palreverse = (document.getElementById("palinput").value).replace(/[^'\w_]/g, '').toLowerCase().split('').reverse().join('');
  if (palin === palreverse) {
      document.getElementById("paloutput").innerHTML = "This is a palindrome"
  } else document.getElementById("paloutput").innerHTML = "This is not a palindrome";
}; 
$("#palinput").on("keyup", function() {
  palindrome();
});
    function longfinder() {
  var longin = document.getElementById("longuestinput").value;
    var x = []
      for (var index = 0; index < longin.split(' ').length; index++) {
    if (x.length < (longin.split(' ')[index]).length) {
      x = (longin.split(' ')[index]);
    }
  }
  document.getElementById("longuestoutput").innerHTML = x;
}; 
$("#longuestinput").on("keyup", function() {
  longfinder();
});
        function repeating() {
  var repeatin = document.getElementById("repeatinput").value
    var repeatinnum = document.getElementById("repeatnumber").value
    var repeatout = "";
      for (var i = 0; i < repeatinnum; i++) {
    repeatout += repeatin.slice();
  }
  document.getElementById("repeatoutput").innerHTML = repeatout;
}; 
$("#repeatinput").on("keyup", function() {
  repeating();
});
$("#repeatnumber").on("change", function() {
  repeating();
});

